    $("#oyuncubildirimi").submit(function(){

      var formID=$(this).attr('id');
      var formDetails=$('#'+formID);

      $.ajax({

        type:"POST",
        url:'../db/process.php',
        data:formDetails.serialize(),
        success: function(data) {

          veri=JSON.parse(data);

          swal("İşlem Sonucu",veri.message,veri.status);          

        }

      });

      return false;

    });
  });
</script>

